package steamid

import "testing"

func TestSteamIDFromTicket(t *testing.T) {
	type args struct {
		ticket string
	}
	tests := []struct {
		name    string
		args    args
		want    uint64
		wantErr bool
	}{
		{
			name: "1",
			args: args{
				ticket: ``,
			},
			want:    76561197960287930,
			wantErr: false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := FromTicket(tt.args.ticket)
			if (err != nil) != tt.wantErr {
				t.Errorf("SteamIDFromTicket() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if got != tt.want {
				t.Errorf("SteamIDFromTicket() got = %v, want %v", got, tt.want)
			}
		})
	}
}
